changeSet.sql:
UPDATE patientsurveys
SET Status = 2
WHERE PreOperativeAssessmentId IN (SELECT Id FROM PreOperativeassessments WHERE IsArchived = 1);

INSERT INTO PatientSurveys(Id, PatientId, PatientDobYear, HospitalId, BodyStructureId, SurgeryDate, MethodId, SurgeonId, PreOperativeAssessmentId, TheaterId, Status)
SELECT UUID(), PatientId, DateOfBirthYear,HospitalId, BodyStructureId, SurgeryDate, MethodId, SurgeonId, Id, TheaterId, 2 FROM PreOperativeassessments 
where isarchived = 1 and id NOT IN (SELECT PreOperativeAssessmentId FROM patientsurveys);
